var UserInfo = Class.create();
UserInfo.prototype = Object.extendsObject(AbstractAjaxProcessor, {
    getUser:function() {
    	var userInfo = gs.getUser();
    	this.addAttribute("result", userInfo);
    },
    getUserID: function() {
      this.addAttribute('id', gs.getUserID());
    },
    getUserName: function() {
      this.addAttribute('username', gs.getUserName());
    }
});
